/**
 * Eases which are not provided by Black Engine.
 */
class Ease {
  // swipe
  static outQuint(t) {
    return 1 + --t * t * t * t * t
  }

  // swipe bounce
  static outQuard(t) {
    return t * (2 - t)
  }

  // bounce
  static outQuart(t) {
    return 1 - --t * t * t * t
  }
}

export default Ease

import Tween from './Tween'

const IN = 'in'
const OUT = 'out'

/**
 * A common animation - fade.
 *
 * @example
 * const fade = new Fade({ type: 'in' })
 * gameObject.addComponent(fade)
 */
class Fade extends Tween {
  /**
   * @param {Object} args
   * @param {string} [args.type='in'] type of animation, valid value: `in` / `out`
   * @param {number} [args.duration=1] duration of animation
   * @param {function} onComplete callback will be called when animation is completed
   */
  constructor({ type = IN, duration = 1 } = {}, onComplete) {
    if (type === IN) {
      super({ alpha: 1 }, duration)
      /**
       * @ignore
       */
      this.startValue = 0
    } else if (type === OUT) {
      super({ alpha: 0 }, duration)
      /**
       * @ignore
       */
      this.startValue = 1
    } else {
      throw new Error(`[Fade] unknown type - ${type}`)
    }

    /**
     * @access private
     */
    this.mOnComplete = onComplete
  }

  /**
   * @ignore
   */
  onAdded(gameObject) {
    if (this.mOnComplete) {
      this.on('complete', () => {
        this.mOnComplete()
      })
    }

    gameObject.alpha = this.startValue
    super.onAdded()
  }
}

export default Fade

import { Tween as $Tween } from 'black-engine'

/**
 * Extended version of Tween provided by Black Engine.
 */
class Tween extends $Tween {
  /**
   * @ignore
   */
  constructor(...args) {
    super(...args)

    /**
     * @access private
     */
    this.mIsCompleted = false
  }

  /**
   * @ignore
   */
  onAdded(gameObject) {
    this.on('complete', () => {
      this.mIsCompleted = true
    })

    super.onAdded(gameObject)
  }

  /**
   * @return {Promise} It will be resolved when current tween is completed.
   */
  complete() {
    return new Promise(resolve => {
      if (this.mIsCompleted) {
        resolve()
      } else {
        this.on('complete', resolve)
      }
    })
  }
}

export default Tween

/**
 * Load vConsole according querystring. When specified pattern is found in
 * querystring, vConsole will be enabled.
 *
 * WARNING: If you are using Babel, you need to enable @babel/plugin-syntax-dynamic-import.
 *
 * @see https://github.com/Tencent/vConsole
 */
class Console {
  /**
   * @param {RegExp} [pattern=/debug/] pattern will be searched
   */
  constructor(pattern = /debug/) {
    if (pattern.test(location.search)) {
      import('vconsole').then(({ default: VConsole }) => {
        new VConsole()
      })
    }
  }
}

export default Console

