import { Black, Component } from 'black-engine'
import Layer from '../Layer'
import { transformDOM } from '../helper/dom'

class DomInteraction extends Component {
  constructor(
    callback,
    { events = ['touchend', 'click'], once = false, debug = false } = {}
  ) {
    super()

    if (!callback || typeof callback !== 'function') {
      throw new Error('[DomInteraction] invalid callback')
    }

    this.mDom = null
    this.mEvents = events
    this.mOnce = once
    this.mTriggeredOnce = false
    this.mCallback = callback
    this.mDebug = debug
  }

  cb = () => {
    if (this.mOnce && this.mTriggeredOnce) return

    this.mTriggeredOnce = true
    this.mCallback()
  }

  onAdded(gameObject) {
    const { containerElement } = Black.instance
    const { width, height } = gameObject

    const dom = document.createElement('div')
    this.mDom = dom
    dom.style.position = 'absolute'
    dom.style.width = `${width}px`
    dom.style.height = `${height}px`
    dom.style.zIndex = Layer.DOM_INTERACTION
    this.position()
    containerElement.appendChild(dom)

    if (this.mDebug) {
      dom.style.backgroundColor = 'rgba(255, 0, 0, 0.2)'
    }

    this.mEvents.forEach(event => {
      dom.addEventListener(event, this.cb)
    })

    Black.instance.stage.on('resize', this.position, this)
  }

  onRemoved() {
    const { containerElement } = Black.instance

    this.mEvents.forEach(event => {
      this.mDom.removeEventListener(event, this.cb)
    })

    containerElement.removeChild(this.mDom)

    Black.instance.stage.off('resize')
  }

  position() {
    const { gameObject, mDom: dom } = this
    const matrix = gameObject.worldTransformation
    transformDOM(dom, matrix)
  }
}

export default DomInteraction

import { Component, GameObject, Input } from 'black-engine'

class NativeInteraction extends Component {
  constructor(callback, { events = ['touchend'] } = {}) {
    super()

    if (!callback || typeof callback !== 'function') {
      throw new Error('[NativeInteraction] invalid callback')
    }

    this.mEvents = events
    this.mCallback = callback
  }

  eventHandler = () => {
    if (GameObject.intersects(this.gameObject, Input.pointerPosition)) {
      this.mCallback()
    }
  }

  onAdded() {
    this.mEvents.forEach(event => {
      document.addEventListener(event, this.eventHandler)
    })
  }

  onRemoved() {
    this.mEvents.forEach(event => {
      document.removeEventListener(event, this.eventHandler)
    })
  }
}

export default NativeInteraction

import { Component } from 'black-engine'

class ScaleButton extends Component {
  constructor(scale = 0.95) {
    super()

    this.mScale = scale
    this.mOnPointerUp = null
    this.mOnPointerDown = null
    this.mOriginalScale = 1
  }

  onAdded(gameObject) {
    this.mOriginalScale = gameObject.scale

    this.mOnPointerUp = gameObject.on('pointerUp', () => {
      gameObject.scale = this.mOriginalScale
    })

    this.mOnPointerDown = gameObject.on('pointerDown', () => {
      gameObject.scale = this.mOriginalScale * this.mScale
    })
  }

  onRemoved() {
    this.mOnPointerUp.off()
    this.mOnPointerDown.off()
  }
}

export default ScaleButton

import { AssetManager, Component } from 'black-engine'

const { default: defaultAssetManager } = AssetManager

class SoundButton extends Component {
  constructor(
    downSoundName,
    upSoundName,
    { assetManager } = { assetManager: defaultAssetManager }
  ) {
    super()

    this.mDownSoundClip =
      (downSoundName && assetManager.getSound(downSoundName)) || null
    this.mUpSoundClip =
      (upSoundName && assetManager.getSound(upSoundName)) || null
    this.mOnPointerUp = null
    this.mOnPointerDown = null
  }

  onAdded(gameObject) {
    this.mOnPointerDown = gameObject.on('pointerDown', () => {
      this.mDownSoundClip.play()
    })

    this.mOnPointerUp = gameObject.on('pointerUp', () => {
      this.mUpSoundClip.play()
    })
  }

  onRemoved() {
    this.mOnPointerDown.off()
    this.mOnPointerUp.off()
  }
}

export default SoundButton

// WARNING: All this layers are used internally, don't use them directly.
export default {
  // place hidden DOMs, like hidden <video>
  DOM_DISPLAY_HIDDEN: -1,
  // place DOMs, like <video>
  DOM_DISPLAY: 1,
  // place canvas of black
  CANVAS: 2,
  // place DOMs for interaction purpose
  DOM_INTERACTION: 3,
}

import { DisplayObject } from 'black-engine'

/**
 * Scene is abstraction built on DisplayObject.
 *
 * @example
 * // Scene is just a normal DisplayObject in Black Engine.
 * class Playground extends Scene {
 *   onAdded() {
 *     // ...
 *   }
 * }
 *
 * @example
 * // If you wanna extend Scene's constructor, please make sure `super(name)`
 * // is called. Because attribute `name` is required by SceneManager.
 * class Playground extends Scene {
 *   constructor(name) {
 *     super(name)
 *
 *     this.toggle = true
 *   }
 * }
 */
class Scene extends DisplayObject {
  /**
   * constructor
   * @param {string} name name of scene
   */
  constructor(name) {
    super()

    this.name = name
  }
}

export default Scene

